import { useState, useEffect } from 'react';
import { Box, Typography, Button, Modal, IconButton } from '@mui/material';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import CloseIcon from '@mui/icons-material/Close';
import PropTypes from 'prop-types';
import './Banner.css'; // Importa el archivo CSS

const Banner = ({ setCategoryFilter, videoSectionRef }) => {
  const [video, setVideo] = useState(null);
  const [openModal, setOpenModal] = useState(false);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await fetch(
          'https://my-json-server.typicode.com/sileneg/aluraflix-database/videos'
        );
        if (response.ok) {
          const data = await response.json();
          setVideo(data[0]);
        } else {
          console.error('Error fetching video:', response.status);
        }
      } catch (error) {
        console.error('Error fetching video:', error);
      }
    };

    fetchVideo();
  }, []);

  if (!video) {
    return <Typography>Cargando...</Typography>;
  }

  let videoId;
  if (video.video.includes('v=')) {
    videoId = video.video.split('v=')[1].split('&')[0];
  } else {
    videoId = video.video.split('/').pop();
  }

  const videoThumbnailUrl = `https://img.youtube.com/vi/${videoId}/hqdefault.jpg`;

  const handleCategoryClick = () => {
    setCategoryFilter('Frontend');
    if (videoSectionRef && videoSectionRef.current) {
      videoSectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Box className="banner-container">
      {/* Línea divisoria azul */}
      <Box className="banner-divider" />

      {/* Contenido principal del Banner */}
      <Box className="banner-content">
        {/* Sección de texto */}
        <Box className="banner-text">
          <Button
            variant="contained"
            onClick={handleCategoryClick}
            className="banner-button"
          >
            Frontend
          </Button>
          <Typography variant="h2" className="banner-title">
            Challenge React
          </Typography>
          <Typography variant="body1" className="banner-description">
            Este challenge es una forma de aprendizaje. Es un mecanismo donde
            podrás comprometerte en la resolución de un problema para poder
            aplicar todos los conocimientos adquiridos en la formación React.
          </Typography>
        </Box>

        {/* Sección del video */}
        <Box
          className="banner-video"
          onClick={() => setOpenModal(true)}
        >
          <img
            src={videoThumbnailUrl}
            alt="Thumbnail"
            className="banner-video-thumbnail"
          />
          <PlayCircleOutlineIcon className="banner-play-icon" />
        </Box>
      </Box>

      {/* Modal del video */}
      <Modal open={openModal} onClose={() => setOpenModal(false)}>
        <Box className="banner-modal">
          <IconButton
            className="banner-close-button"
            onClick={() => setOpenModal(false)}
          >
            <CloseIcon className="banner-close-icon" />
          </IconButton>
          <iframe
            width="100%"
            height="100%"
            src={`https://www.youtube.com/embed/${videoId}`}
            title="YouTube video player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </Box>
      </Modal>
    </Box>
  );
};

Banner.propTypes = {
  setCategoryFilter: PropTypes.func.isRequired,
  videoSectionRef: PropTypes.object.isRequired,
};

export default Banner;
